import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Layout from '../components/Layout';
import '../components/Layout.css';
import '../styles/main.css';
import axios from 'axios';

function DashboardDoctor() {
  const username = localStorage.getItem('username');
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    // Fetch today's appointments for the doctor
    axios.get('http://localhost:8000/api/accounts/doctor-appointments-today/', { withCredentials: true })
      .then((res) => setAppointments(res.data))
      .catch((err) => console.error("Error fetching doctor appointments:", err));
  }, []);

  return (
    <Layout role="doctor">
      <div className="dashboard-content">
        <h2>Welcome, Dr. {username}</h2>
        <p>Today's appointments:</p>

        {appointments.length === 0 ? (
          <p>No appointments scheduled for today.</p>
        ) : (
          <ul>
            {appointments.map((appointment, index) => (
              <li key={index}>
                <strong>{appointment.patient}</strong> at {appointment.time} on {appointment.date}
              </li>
            ))}
          </ul>
        )}

        <div className="action-buttons">
          <Link to="/doctor-appointments">
            <button className="btn-primary">ðŸ“‹ View Appointments</button>
          </Link>
        </div>
      </div>
    </Layout>
  );
}

export default DashboardDoctor;
