import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Layout.css';

function Layout({ children, role }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    navigate('/login');
  };

  return (
    <div className="layout">
      <div className="sidebar">
        <h3>Menu</h3>
        <nav>
          <Link to={role === 'doctor' ? '/dashboard/doctor' : '/dashboard/patient'}>üè† Dashboard</Link>

          {role === 'patient' && (
            <Link to="/book-appointment">üìÖ Book Appointment</Link>
          )}

          {role === 'doctor' && (
            <Link to="#">üìã View Appointments</Link> // You can replace this with route later
          )}
        </nav>
      </div>

      <div className="main">
        <header className="topbar">
          <div className="brand">Healthcare App</div>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </header>
        <div className="content">{children}</div>
      </div>
    </div>
  );
}

export default Layout;
