import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import axios from 'axios';
import '../styles/main.css';

function DashboardDoctor() {
  const [appointments, setAppointments] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios
      .get('http://localhost:8000/api/accounts/doctor-appointments-today/', { withCredentials: true })
      .then((res) => {
        if (res.data.length > 0) {
          setAppointments(res.data);
        } else {
          setMessage('No appointments for today.');
        }
      })
      .catch((err) => {
        setMessage('Failed to load appointments. Please try again.');
        console.error(err);
      });
  }, []);

  return (
    <Layout role="doctor">
      <h2>Welcome, Doctor</h2>
      <p>Today's appointments:</p>
      {message && <p className="message">{message}</p>}
      <ul>
        {appointments.length > 0 ? (
          appointments.map((appt, index) => (
            <li key={index}>
              {appt.patient} - {appt.date} at {appt.time}
            </li>
          ))
        ) : (
          <p>No appointments today.</p>
        )}
      </ul>
    </Layout>
  );
}

export default DashboardDoctor;
