import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout';
import axios from 'axios';
import '../components/Layout.css';

function DashboardDoctor() {
  const [appointments, setAppointments] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios
      .get('http://localhost:8000/api/accounts/doctor-appointments-today/', { withCredentials: true })
      .then((res) => {
        setAppointments(res.data);
      })
      .catch((err) => {
        setMessage('Failed to load appointments. Please try again.');
        console.error(err);
      });
  }, []);

  return (
    <Layout role="doctor">
      <h2>Welcome, Doctor</h2>
      {message && <p>{message}</p>}
      {appointments.length > 0 ? (
        <div>
          <h3>Today's Appointments</h3>
          <ul>
            {appointments.map((appointment, index) => (
              <li key={index}>
                <strong>{appointment.patient}</strong> - {appointment.date} at {appointment.time}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No appointments for today.</p>
      )}
    </Layout>
  );
}

export default DashboardDoctor;
